mapper.insert:
INSERT INTO room ( room_no, room_type, bed_type, room_price, package_id, hotel_category
) VALUES (
	#{roomNo}, #{roomType}, #{bedType}, #{roomPrice}, #{packageId}, #{hotelCate}
)

mapper.select:
SELECT id, room_no, room_type, bed_type, room_price, package_id, hotel_category FROM room
room_type LIKE concat('%', #{roomType}, '%')
ORDER BY room DESC LIMIT #{limitStart}, #{listCount}
